      const newGuardianId = generateNewGuardianId(existingIds);

      // Tạo mã ngẫu nhiên cho link mời
      const inviteCode = generateRandomCode(8);

      // Tạo link mời với URL ngrok từ biến môi trường hoặc sử dụng địa chỉ hiện tại
