      // Skip 8 bytes đầu tiên (discriminator)
      const data = guardianAccount.data.slice(8);

      // Parse wallet (Pubkey) - 32 bytes
      let currentOffset = 0;
      const wallet = new PublicKey(data.slice(currentOffset, currentOffset + 32));
      currentOffset += 32;

      // Parse guardian_id (u64) - 8 bytes
      const guardianIdBytes = data.slice(currentOffset, currentOffset + 8);
      currentOffset += 8;
      const guardianId = new BN(guardianIdBytes, 'le').toNumber();

      // Parse name (String) - 4 bytes cho length + data
      const nameLength = data.readUInt32LE(currentOffset);
      currentOffset += 4;

      if (nameLength > 0 && currentOffset + nameLength <= data.length) {
        const nameBytes = data.slice(currentOffset, currentOffset + nameLength);
        const name = new TextDecoder().decode(nameBytes);
        currentOffset += nameLength;

        // Parse is_active (bool) - 1 byte
        const isActive = data[currentOffset] === 1;
        currentOffset += 1;

        // Parse recovery_hash ([u8; 32]) - 32 bytes
        const recoveryHash = data.slice(currentOffset, currentOffset + 32);
        const recoveryHashHex = Buffer.from(recoveryHash).toString('hex');
        currentOffset += 32;

        // Parse is_owner (bool) - 1 byte
        const isOwner = data[currentOffset] === 1;
        currentOffset += 1;

        // Parse webauthn_pubkey (Option<[u8; 33]>) - 1 byte + optionally 33 bytes
        const hasWebauthn = data[currentOffset] === 1;
        currentOffset += 1;

        let webauthnPubkey: Uint8Array | null = null;
        if (hasWebauthn) {
          webauthnPubkey = data.slice(currentOffset, currentOffset + 33);
          const webauthnHex = Buffer.from(webauthnPubkey).toString('hex');
          currentOffset += 33;
        }

        // Parse bump (u8) - 1 byte
        const bump = data[currentOffset];

