        const guardianInfoObj = {
          pda: guardianPDA.toString(),
          wallet: wallet.toString(),
          guardianId,
          name,
          isActive,
          recoveryHash: recoveryHashHex,
          isOwner,
          webauthnPubkey: webauthnPubkey ? Buffer.from(webauthnPubkey).toString('hex') : null,
          bump
        };

