import React, { useState } from 'react';
import { PublicKey } from '@solana/web3.js';
import { signWithWebAuthnAndCreateInstruction } from '../utils/webauthnSigningUtils';

interface TransferFormProps {
  onTransfer: (recipient: string, amount: number, signatureInstruction?: any) => Promise<void>;
  isProcessing: boolean;
  balance: number;
}

const TransferForm: React.FC<TransferFormProps> = ({ onTransfer, isProcessing, balance }) => {
  const [recipient, setRecipient] = useState<string>('');
  const [amount, setAmount] = useState<number>(0.01);
  const [error, setError] = useState<string | null>(null);
  const [useSignature, setUseSignature] = useState<boolean>(true);
  const [isSigningWithWebAuthn, setIsSigningWithWebAuthn] = useState<boolean>(false);

  const validateRecipient = (address: string): boolean => {
    try {
      new PublicKey(address);
      return true;
    } catch (error) {
      return false;
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError(null);

    // Kiểm tra địa chỉ người nhận
    if (!recipient || !validateRecipient(recipient)) {
      setError('Địa chỉ người nhận không hợp lệ');
      return;
    }

    // Kiểm tra số tiền
    if (!amount || amount <= 0) {
      setError('Vui lòng nhập số tiền hợp lệ');
      return;
    }

    // Kiểm tra số dư
    if (amount > balance) {
      setError(`Số dư không đủ. Bạn chỉ có ${balance} SOL`);
      return;
    }

    // Gọi hàm chuyển tiền
    try {
      // Tạo message để ký
      const messageToSign = `Chuyển ${amount} SOL đến địa chỉ ${recipient} vào thời gian ${new Date().toISOString()}`;

      // Tạo instruction ký nếu được yêu cầu
      let signatureInstruction;
      if (useSignature) {
        try {
          setIsSigningWithWebAuthn(true);
          // Hiển thị thông báo cho người dùng biết họ sẽ được yêu cầu ký
          setError('Đang yêu cầu chữ ký của bạn... Vui lòng xác nhận trên thiết bị của bạn.');

          // Sử dụng WebAuthn để ký thay vì tạo cặp khóa ngẫu nhiên
          signatureInstruction = await signWithWebAuthnAndCreateInstruction(messageToSign);
          console.log("Đã nhận chữ ký WebAuthn thành công:", signatureInstruction);

          // Xóa thông báo đang ký
          setError(null);
        } catch (signError) {
          console.error("Lỗi khi tạo chữ ký:", signError);
          setError("Lỗi khi tạo chữ ký: " + (signError instanceof Error ? signError.message : String(signError)));
          setIsSigningWithWebAuthn(false);
          return;
        }
        setIsSigningWithWebAuthn(false);
      }

      // Gọi hàm chuyển tiền với chữ ký (nếu có)
      await onTransfer(recipient, amount, signatureInstruction);

      // Reset form sau khi gửi thành công
      setRecipient('');  // Xóa trường địa chỉ người nhận
      setAmount(0.01);   // Reset số tiền về giá trị mặc định
      // Hiển thị thông báo thành công
      setError('✅ Chuyển tiền thành công!');
    } catch (error: any) {
      setError(error.message || 'Có lỗi xảy ra khi chuyển tiền');
    }
  };

  return (
    <div className="transfer-form">
      <h2 className="text-xl font-bold mb-4">Chuyển Tiền</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group mb-3">
          <label htmlFor="recipient">Địa chỉ người nhận:</label>
          <input
            type="text"
            id="recipient"
            className="form-control w-full p-2 border rounded mb-2"
            value={recipient}
            onChange={(e) => setRecipient(e.target.value)}
            placeholder="Nhập địa chỉ Solana hợp lệ"
            disabled={isProcessing || isSigningWithWebAuthn}
            required
          />
        </div>

        <div className="form-group mb-3">
          <label htmlFor="amount">Số lượng SOL:</label>
          <input
            type="number"
            id="amount"
            className="form-control w-full p-2 border rounded mb-2"
            value={amount}
            onChange={(e) => setAmount(Number(e.target.value))}
            min="0.000001"
            step="any"
            disabled={isProcessing || isSigningWithWebAuthn}
            required
          />
          <small>Số dư hiện tại: {balance} SOL</small>
        </div>

        <div className="form-group mb-3">
          <div className="flex items-center">
            <input
              type="checkbox"
              id="useSignature"
              checked={useSignature}
              onChange={(e) => setUseSignature(e.target.checked)}
              className="mr-2"
              disabled={isProcessing || isSigningWithWebAuthn}
            />
            <label htmlFor="useSignature">Sử dụng chữ ký WebAuthn</label>
          </div>
          <small className="text-gray-600">
            {useSignature 
              ? 'Bạn sẽ được yêu cầu xác nhận giao dịch bằng sinh trắc học (TouchID/FaceID)' 
              : 'Bỏ qua việc ký giao dịch bằng WebAuthn'}
          </small>
        </div>

        {error && (
          <div className={`mb-3 p-2 rounded ${error.startsWith('✅') ? 'bg-green-100 text-green-800' : error.startsWith('Đang yêu cầu') ? 'bg-blue-100 text-blue-800' : 'bg-red-100 text-red-800'}`}>
            {error}
          </div>
        )}

        <button 
          type="submit" 
          disabled={isProcessing || isSigningWithWebAuthn}
          className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600 w-full disabled:bg-gray-400"
        >
          {isProcessing ? 'Đang xử lý...' : isSigningWithWebAuthn ? 'Đang yêu cầu chữ ký...' : 'Chuyển Tiền'}
        </button>
      </form>
    </div>
  );
};

export default TransferForm;
