// Tạo hàm mới createAddGuardianTx
export const createAddGuardianTx = (
  multisigPDA: PublicKey,
  guardianPDA: PublicKey,
  guardianPubkey: PublicKey,
  guardianName: string,
  recoveryHash: Uint8Array,
  isOwner: boolean,
  webauthnPubkey?: Buffer
): Transaction => {
  try {
    // Discriminator cho add_guardian
    const discriminator = Buffer.from([167, 189, 170, 27, 74, 240, 201, 241]);

    // Tạo buffer cho tên guardian
    const nameBuffer = Buffer.from(guardianName);
    const nameLenBuffer = Buffer.alloc(4);
    nameLenBuffer.writeUInt32LE(nameBuffer.length, 0);

    // Tạo buffer cho các tham số
    const isOwnerByte = Buffer.from([isOwner ? 1 : 0]);

    // Tạo buffers cho instruction data
    const dataBuffers = [
      discriminator,
      guardianPubkey.toBuffer(),
      nameLenBuffer,
      nameBuffer,
      Buffer.from(recoveryHash)
    ];

    // Thêm isOwner
    dataBuffers.push(isOwnerByte);

    // Xử lý webauthn_pubkey (option)
    if (webauthnPubkey && isOwner) {
      // Some variant (1)
      dataBuffers.push(Buffer.from([1]));

      // Nén khóa công khai nếu cần
      let compressedKey: Buffer;
      if (webauthnPubkey.length === 65 && webauthnPubkey[0] === 0x04) {
        // Khóa không nén, cần nén lại
        compressedKey = compressPublicKey(webauthnPubkey);
      } else if (webauthnPubkey.length === 33 && (webauthnPubkey[0] === 0x02 || webauthnPubkey[0] === 0x03)) {
        // Khóa đã nén, sử dụng trực tiếp
        compressedKey = webauthnPubkey;
      } else {
        throw new Error(`Khóa công khai WebAuthn không đúng định dạng: ${webauthnPubkey.length} bytes`);
      }

      dataBuffers.push(compressedKey);
    } else {
      // None variant (0)
      dataBuffers.push(Buffer.from([0]));
    }

    // Nối tất cả buffer lại với nhau
    const data = Buffer.concat(dataBuffers.map(buffer => new Uint8Array(buffer)));

    // Tạo instruction
    const instruction = new TransactionInstruction({
      keys: [
        { pubkey: multisigPDA, isSigner: false, isWritable: true },
        { pubkey: guardianPDA, isSigner: false, isWritable: true },
        { pubkey: guardianPubkey, isSigner: true, isWritable: true },
        { pubkey: SystemProgram.programId, isSigner: false, isWritable: false }
      ],
      programId: programID,
      data
    });

    return new Transaction().add(instruction);
  } catch (error) {
    console.error("Lỗi khi tạo transaction add guardian:", error);
    throw error;
  }
};

// Thêm các hằng số cho curve secp256r1 - dựa trên file test đã hoạt động
const SIGNATURE_OFFSETS_SERIALIZED_SIZE = 14;
const SIGNATURE_OFFSETS_START = 2;
const DATA_START = SIGNATURE_OFFSETS_SERIALIZED_SIZE + SIGNATURE_OFFSETS_START;
const SIGNATURE_SERIALIZED_SIZE = 64;
const COMPRESSED_PUBKEY_SERIALIZED_SIZE = 33;

// Order của curve secp256r1
const SECP256R1_ORDER = new Uint8Array([
  0xff, 0xff, 0xff, 0xff, 0x00, 0x00, 0x00, 0x00, 0xff, 0xff, 0xff, 0xff, 0xff,
  0xff, 0xff, 0xff, 0xbc, 0xe6, 0xfa, 0xad, 0xa7, 0x17, 0x9e, 0x84, 0xf3, 0xb9,
  0xca, 0xc2, 0xfc, 0x63, 0x25, 0x51,
]);

// Nửa order của curve secp256r1
const SECP256R1_HALF_ORDER = new Uint8Array([
  0x7f, 0xff, 0xff, 0xff, 0x80, 0x00, 0x00, 0x00, 0x7f, 0xff, 0xff, 0xff, 0xff,
  0xff, 0xff, 0xff, 0xde, 0x73, 0x7d, 0x56, 0xd3, 0x8b, 0xcf, 0x42, 0x79, 0xdc,
  0xe5, 0x61, 0x7e, 0x31, 0x92, 0xa8,
]);

// Thêm hàm so sánh số lớn
function isGreaterThan(a: Uint8Array | Buffer, b: Uint8Array): boolean {
  const aBuf = Buffer.isBuffer(a) ? a : Buffer.from(a);
  if (aBuf.length !== b.length) {
    return aBuf.length > b.length;
  }
  for (let i = 0; i < aBuf.length; i++) {
    if (aBuf[i] !== b[i]) {
      return aBuf[i] > b[i];
    }
  }
  return false;
}

// Sửa lại hàm subtractBigNumbers để xử lý big-endian đúng cách
function subtractBigNumbers(a: Uint8Array, b: Uint8Array | Buffer): Uint8Array {
  const bBuf = Buffer.isBuffer(b) ? b : Buffer.from(b);
  const result = new Uint8Array(a.length);
  let borrow = 0;

  // Đảm bảo cả a và b đều có đúng 32 bytes
  if (a.length !== 32 || bBuf.length !== 32) {
    console.error(`Độ dài không đúng: a=${a.length}, b=${bBuf.length}, phải là 32 bytes`);
  }

  // Tính từ byte ít quan trọng nhất đến quan trọng nhất (big endian)
  for (let i = a.length - 1; i >= 0; i--) {
    let diff = a[i] - bBuf[i] - borrow;
    if (diff < 0) {
      diff += 256;
      borrow = 1;
    } else {
      borrow = 0;
    }
    result[i] = diff;
  }

  // Log giá trị input và output để debug
  console.log('Input a (hex):', Buffer.from(a).toString('hex'));
  console.log('Input b (hex):', Buffer.from(bBuf).toString('hex'));
  console.log('Result (hex):', Buffer.from(result).toString('hex'));

  return result;
}

/**
 * Tạo instruction cho chương trình Secp256r1SigVerify
 * Cài đặt dựa trên file test đã hoạt động thành công
 * @param message Message đã hash (32 bytes)
 * @param publicKey Khóa công khai secp256r1 (dạng nén, 33 bytes)
 * @param signature Chữ ký (64 bytes)
 */
export const createSecp256r1Instruction = (
  message: Buffer,
  publicKey: Buffer,
  signature: Buffer
): web3.TransactionInstruction => {
  try {
    console.log('Tạo secp256r1 instruction không chuẩn hóa s...');
    console.log('- Message length:', message.length, 'bytes:', message.toString('hex'));
    console.log('- Public key length:', publicKey.length, 'bytes:', publicKey.toString('hex'));
    console.log('- Signature length:', signature.length, 'bytes:', signature.toString('hex'));

    // Kiểm tra kích thước
    if (message.length !== 32) {
      throw new Error(`Message phải có độ dài 32 bytes, nhận được ${message.length} bytes`);
    }

    if (publicKey.length !== COMPRESSED_PUBKEY_SERIALIZED_SIZE) {
      throw new Error(`Public key phải có độ dài 33 bytes (dạng nén), nhận được ${publicKey.length} bytes`);
    }

    if (signature.length !== SIGNATURE_SERIALIZED_SIZE) {
      throw new Error(`Signature phải có độ dài 64 bytes, nhận được ${signature.length} bytes`);
    }

    // Tách r và s từ signature nhưng KHÔNG thực hiện chuẩn hóa s
    // QUAN TRỌNG: Đây là thay đổi chính - không chuẩn hóa s
    const r = signature.slice(0, 32);
    const s = signature.slice(32, 64);

    console.log('Signature components:');
    console.log('- r (hex):', r.toString('hex'));
    console.log('- s (hex):', s.toString('hex'));

    // KHI DEBUG BỎ QUA BƯỚC CHUẨN HÓA S
    // if (isGreaterThan(s, SECP256R1_HALF_ORDER)) {
    //   console.log('Chuẩn hóa signature: s > HALF_ORDER, thực hiện s = ORDER - s');
    //   const newS = subtractBigNumbers(SECP256R1_ORDER, s);
    //   signature = Buffer.concat([r, Buffer.from(newS)]);
    //   console.log('Signature sau khi chuẩn hóa (hex):', signature.toString('hex'));
    // } else {
    //   console.log('Signature đã chuẩn hóa, không cần thay đổi');
    // }

    console.log('DEBUG: Sử dụng signature gốc không chuẩn hóa');

    // Tính toán tổng kích thước instruction data
    const totalSize = DATA_START + SIGNATURE_SERIALIZED_SIZE + COMPRESSED_PUBKEY_SERIALIZED_SIZE + message.length;
    const instructionData = Buffer.alloc(totalSize);

    // Header - giống mẫu
    instructionData.writeUInt8(1, 0); // num_signatures = 1
    instructionData.writeUInt8(0, 1); // padding

    // Tính offset theo mẫu thành công
    const publicKeyOffset = DATA_START;
    const signatureOffset = publicKeyOffset + COMPRESSED_PUBKEY_SERIALIZED_SIZE;
    const messageDataOffset = signatureOffset + SIGNATURE_SERIALIZED_SIZE;

    // Ghi offset vào instructionData - chính xác như mẫu
    instructionData.writeUInt16LE(signatureOffset, SIGNATURE_OFFSETS_START);
    instructionData.writeUInt16LE(0xffff, SIGNATURE_OFFSETS_START + 2);
    instructionData.writeUInt16LE(publicKeyOffset, SIGNATURE_OFFSETS_START + 4);
    instructionData.writeUInt16LE(0xffff, SIGNATURE_OFFSETS_START + 6);
    instructionData.writeUInt16LE(messageDataOffset, SIGNATURE_OFFSETS_START + 8);
    instructionData.writeUInt16LE(message.length, SIGNATURE_OFFSETS_START + 10);
    instructionData.writeUInt16LE(0xffff, SIGNATURE_OFFSETS_START + 12);

    // Copy data vào đúng vị trí theo mẫu
    publicKey.copy(instructionData, publicKeyOffset);    // Public key trước
    signature.copy(instructionData, signatureOffset);    // Signature tiếp theo
    message.copy(instructionData, messageDataOffset);    // Message cuối cùng

    console.log('Secp256r1 instruction data:');
    console.log(`- Total size: ${instructionData.length} bytes`);
    console.log(`- Public key offset: ${publicKeyOffset}`);
    console.log(`- Signature offset: ${signatureOffset}`);
    console.log(`- Message offset: ${messageDataOffset}`);
    console.log(`- Instruction data (hex):`, instructionData.toString('hex').substring(0, 100) + '...');

    return new web3.TransactionInstruction({
      keys: [],
      programId: SECP256R1_PROGRAM_ID,
      data: instructionData
    });
  } catch (error) {
    console.error('❌ Lỗi khi tạo secp256r1 instruction:', error);
    throw error;
  }
};

/**
 * Kiểm tra xem chữ ký secp256r1 có hợp lệ không bằng cách mô phỏng transaction
 * @param connection Kết nối đến Solana cluster
 * @param publicKey Khóa công khai (dạng nén) dùng để xác minh
 * @param signature Chữ ký (raw format, 64 bytes) cần xác minh
 * @param message Message đã hash (32 bytes) đã được ký
 * @returns Promise<{isValid: boolean, error?: string}> Kết quả kiểm tra và lỗi nếu có
 */
export const testSecp256r1Signature = async (
  connection: web3.Connection,
  publicKey: Buffer,
  signature: Buffer,
  message: Buffer
): Promise<{isValid: boolean, error?: string}> => {
  try {
    // Hiển thị thông tin chi tiết để debug
    console.log('=== DEBUG: testSecp256r1Signature ===');
    console.log('- Message (hex):', message.toString('hex'));
    console.log('- Public key (hex):', publicKey.toString('hex'));
    console.log('- Signature (hex):', signature.toString('hex'));

    // Log: Xác minh các giá trị đầu vào
    if (message.length !== 32) console.warn('⚠️ WARNING: Message không phải 32 bytes');
    if (publicKey.length !== 33) console.warn('⚠️ WARNING: Public key không phải 33 bytes');
    if (signature.length !== 64) console.warn('⚠️ WARNING: Signature không phải 64 bytes');

    if (publicKey[0] !== 0x02 && publicKey[0] !== 0x03) {
      console.warn('⚠️ WARNING: Public key không ở định dạng nén đúng (byte đầu phải là 0x02 hoặc 0x03)');
    }

    // Kiểm tra chữ ký locally trước
    const isValidLocally = verifySecp256r1SignatureLocally(message, publicKey, signature);

    // Nếu không hợp lệ ngoại tuyến, không cần gửi đến Solana
    if (!isValidLocally) {
      console.error('❌ Chữ ký không hợp lệ qua kiểm tra ngoại tuyến');
      return { isValid: false, error: 'Chữ ký không hợp lệ trong kiểm tra ngoại tuyến' };
    }

    console.log('✅ Chữ ký hợp lệ qua kiểm tra ngoại tuyến, tiếp tục kiểm tra bằng Solana...');

    // Thử sử dụng signature gốc và signature đã chuẩn hóa
    let signatureToUse = signature;

    // Tạo instruction secp256r1 để kiểm tra - QUAN TRỌNG: thứ tự tham số đúng
    const testInstruction = createSecp256r1Instruction(
      message,         // Message trước
      publicKey,       // Public key tiếp theo
      signatureToUse   // Signature cuối cùng
    );

    // Tạo một feePayer giả lập để dùng cho transaction test
    const feePayer = web3.Keypair.generate();

    console.log('Kiểm tra chữ ký secp256r1 với transaction riêng biệt...');
    console.log('Tạo instruction secp256r1 thành công:', {
      publicKeyLength: publicKey.length,
      signatureLength: signatureToUse.length,
      messageLength: message.length
    });

    // Tiến hành airdrop SOL cho feePayer trước khi mô phỏng
    try {
      const airdropSignature = await connection.requestAirdrop(
        feePayer.publicKey,
        web3.LAMPORTS_PER_SOL / 100 // Chỉ cần một lượng nhỏ SOL
      );

      // Đợi xác nhận airdrop
      await connection.confirmTransaction(airdropSignature);
      console.log('Đã airdrop SOL cho fee payer để mô phỏng');

      // Xác nhận số dư
      const balance = await connection.getBalance(feePayer.publicKey);
      console.log(`Số dư feePayer: ${balance / web3.LAMPORTS_PER_SOL} SOL`);

      if (balance === 0) {
        throw new Error('Không thể airdrop SOL');
      }
    } catch (airdropError) {
      console.warn('Lỗi khi airdrop SOL:', airdropError);
      console.log('Tiếp tục mô phỏng mà không có airdrop...');
      // Không return ở đây, vẫn tiếp tục mô phỏng
    }

    // Tạo transaction riêng chỉ với instruction secp256r1
    const testTx = new web3.Transaction().add(testInstruction);
    testTx.feePayer = feePayer.publicKey;

    // Thiết lập blockhash cho transaction
    const { blockhash } = await connection.getLatestBlockhash();
    testTx.recentBlockhash = blockhash;

    // Ký transaction bằng feePayer
    testTx.sign(feePayer);

    // Debug: In ra transaction serialized
    const txSerialized = testTx.serialize().toString('base64');
    console.log('Transaction serialized (base64):', txSerialized.substring(0, 100) + '...');

    try {
      // Mô phỏng transaction để kiểm tra (không gửi lên blockchain)
      console.log('Đang mô phỏng transaction...');
      const simulationResult = await connection.simulateTransaction(testTx);

      // In ra kết quả mô phỏng đầy đủ để debug
      console.log('Kết quả mô phỏng đầy đủ:', JSON.stringify(simulationResult, null, 2));

      // Kiểm tra kết quả mô phỏng
      if (simulationResult.value.err) {
        // Có lỗi, kiểm tra xem có phải lỗi chữ ký không
        const errorString = JSON.stringify(simulationResult.value.err);

        console.error('Lỗi khi mô phỏng chữ ký secp256r1:', errorString);

        // Nếu là lỗi Custom:2, đó là lỗi định dạng
        if (errorString.includes('Custom":2')) {
          console.error('❌ Lỗi định dạng (Custom:2) - Có thể do thứ tự dữ liệu không đúng');

          // THỬ NGHIỆM: Thay đổi thứ tự dữ liệu và thử lại
          console.log('Thử lại với thứ tự dữ liệu khác...');

          // Tạo instruction mới với thứ tự dữ liệu khác
          const alternativeInstruction = createAlternativeSecp256r1Instruction(
            message, publicKey, signature
          );

          // Tạo transaction mới
          const altTx = new web3.Transaction().add(alternativeInstruction);
          altTx.feePayer = feePayer.publicKey;
          altTx.recentBlockhash = blockhash;
          altTx.sign(feePayer);

          try {
            console.log('Đang mô phỏng transaction thay thế...');
            const altSimResult = await connection.simulateTransaction(altTx);

            if (!altSimResult.value.err) {
              console.log('✅ Mô phỏng thay thế thành công!');
              return { isValid: true };
            } else {
              console.error('❌ Mô phỏng thay thế cũng thất bại:', JSON.stringify(altSimResult.value.err));
            }
          } catch (e) {
            console.error('Lỗi khi mô phỏng transaction thay thế:', e);
          }

          return { isValid: false, error: 'Lỗi định dạng signature hoặc message: ' + errorString };
        }

        // Nếu là lỗi Custom:3, đó là lỗi xác minh chữ ký
        if (errorString.includes('Custom":3')) {
          return { isValid: false, error: 'Chữ ký không hợp lệ (xác minh thất bại): ' + errorString };
        }

        // Nếu lỗi chứa các string liên quan đến chữ ký không hợp lệ
        if (errorString.includes('signature') && 
            (errorString.includes('invalid') || errorString.includes('verification failed'))) {
          return { isValid: false, error: 'Chữ ký không hợp lệ: ' + errorString };
        }

        // Có lỗi khác không liên quan đến chữ ký
        return { isValid: false, error: 'Lỗi khi mô phỏng transaction: ' + errorString };
      }

      // Không có lỗi, chữ ký hợp lệ
      console.log('Kiểm tra chữ ký secp256r1 thành công!');
      return { isValid: true };
    } catch (simError: any) {
      // Xử lý lỗi mô phỏng
      console.error('Lỗi khi mô phỏng transaction:', simError);

      // Kiểm tra nếu lỗi là AccountNotFound
      if (simError.toString().includes('AccountNotFound')) {
        console.log('Lỗi AccountNotFound: Đây là lỗi mô phỏng, không phải lỗi chữ ký');
        console.log('Tiếp tục với giả định chữ ký hợp lệ...');

        // Trả về kết quả giả định chữ ký hợp lệ, nhưng có ghi chú về lỗi
        return { 
          isValid: true, 
          error: 'Không thể xác minh hoàn toàn do lỗi AccountNotFound trong mô phỏng'
        };
      }

      // Các lỗi khác
      return { isValid: false, error: simError.toString() };
    }
  } catch (error: any) {
    console.error('Lỗi khi kiểm tra chữ ký secp256r1:', error);
    return { isValid: false, error: error.toString() };
  }
};

// Thử nghiệm với một instruction thay thế có thứ tự dữ liệu khác
function createAlternativeSecp256r1Instruction(
  message: Buffer,
  publicKey: Buffer,
  signature: Buffer
): web3.TransactionInstruction {
  try {
    console.log('Tạo instruction secp256r1 thay thế...');

    // Cách cũ: publicKey -> signature -> message
    // Cách mới: thử message -> signature -> publicKey

    const totalSize = DATA_START + SIGNATURE_SERIALIZED_SIZE + COMPRESSED_PUBKEY_SERIALIZED_SIZE + message.length;
    const instructionData = Buffer.alloc(totalSize);

    // Header
    instructionData.writeUInt8(1, 0); // num_signatures
    instructionData.writeUInt8(0, 1); // padding

    // Thử thay đổi offsets
    const messageOffset = DATA_START;
    const signatureOffset = messageOffset + message.length;
    const publicKeyOffset = signatureOffset + SIGNATURE_SERIALIZED_SIZE;

    // Ghi offsets
    instructionData.writeUInt16LE(signatureOffset, SIGNATURE_OFFSETS_START);
    instructionData.writeUInt16LE(0xffff, SIGNATURE_OFFSETS_START + 2);
    instructionData.writeUInt16LE(publicKeyOffset, SIGNATURE_OFFSETS_START + 4);
    instructionData.writeUInt16LE(0xffff, SIGNATURE_OFFSETS_START + 6);
    instructionData.writeUInt16LE(messageOffset, SIGNATURE_OFFSETS_START + 8);
    instructionData.writeUInt16LE(message.length, SIGNATURE_OFFSETS_START + 10);
    instructionData.writeUInt16LE(0xffff, SIGNATURE_OFFSETS_START + 12);

    // Copy data
    message.copy(instructionData, messageOffset);       // Message đầu tiên
    signature.copy(instructionData, signatureOffset);   // Signature tiếp theo
    publicKey.copy(instructionData, publicKeyOffset);   // Public key cuối cùng

    console.log('Thứ tự mới: message -> signature -> publicKey');

    return new web3.TransactionInstruction({
      keys: [],
      programId: SECP256R1_PROGRAM_ID,
      data: instructionData
    });
  } catch (error) {
    console.error('Lỗi khi tạo instruction thay thế:', error);
    throw error;
  }
}

// Hàm kiểm tra chữ ký secp256r1 ngoại tuyến trước khi gửi đến Solana
export const verifySecp256r1SignatureLocally = (
  message: Buffer, 
  publicKey: Buffer, 
  signature: Buffer
): boolean => {
  try {
    // Tạo một instance của elliptic cho curve p256 (secp256r1)
    const ec = new elliptic.ec('p256');

    console.log('=== KIỂM TRA CHỮ KÝ SECP256R1 LOCALLY ===');
    console.log('Message (hex):', message.toString('hex'));
    console.log('Public key (hex):', publicKey.toString('hex'));
    console.log('Signature (hex):', signature.toString('hex'));

    // Kiểm tra độ dài public key
    if (publicKey.length !== 33 || (publicKey[0] !== 0x02 && publicKey[0] !== 0x03)) {
      console.error('Public key phải ở định dạng nén (33 bytes) bắt đầu bằng 0x02 hoặc 0x03');
      return false;
    }

    // Kiểm tra độ dài signature
    if (signature.length !== 64) {
      console.error('Signature phải có độ dài 64 bytes');
      return false;
    }

    // Kiểm tra độ dài message
    if (message.length !== 32) {
      console.error('Message phải có độ dài 32 bytes (đã hash)');
      return false;
    }

    // Tách r và s từ signature (mỗi phần 32 bytes)
    const r = signature.slice(0, 32);
    const s = signature.slice(32, 64);

    // Log giá trị r và s để debug
    console.log('Signature r (hex):', r.toString('hex'));
    console.log('Signature s (hex):', s.toString('hex'));

    // Khôi phục khóa công khai từ buffer
    const ecKey = ec.keyFromPublic(publicKey);

    // Xác thực chữ ký
    const isValid = ec.verify(
      message,
      { r: r.toString('hex'), s: s.toString('hex') },
      ecKey
    );

    console.log('Kết quả xác thực locally:', isValid ? '✅ HỢP LỆ' : '❌ KHÔNG HỢP LỆ');

    return isValid;
  } catch (error) {
    console.error('Lỗi khi xác thực chữ ký locally:', error);
    return false;
  }
};

/**
 * Tạo transaction để chuyển tiền
 * @param multisigPDA PDA của ví multisig
 * @param guardianPDA PDA của guardian
 * @param destination Địa chỉ đích để chuyển token
 * @param amountLamports Số lượng lamports để chuyển
 * @param nonce Nonce tránh replay attack
 * @param timestamp Timestamp cho giao dịch
 * @param message Thông điệp gốc (chưa hash)
 * @param payer Người trả phí giao dịch
 */
export const createTransferTx = (
  multisigPDA: PublicKey,
  guardianPDA: PublicKey,
  destination: PublicKey,
  amountLamports: number,
  nonce: number,
  timestamp: number,
  message: Uint8Array,
  payer: PublicKey
): Transaction => {
  try {
    // Kiểm tra các input
    if (!(multisigPDA instanceof PublicKey)) {
      throw new Error(`multisigPDA không phải PublicKey: ${typeof multisigPDA}`);
    }
    if (!(guardianPDA instanceof PublicKey)) {
      throw new Error(`guardianPDA không phải PublicKey: ${typeof guardianPDA}`);
    }
    if (!(destination instanceof PublicKey)) {
      throw new Error(`destination không phải PublicKey: ${typeof destination}`);
    }
    if (!(payer instanceof PublicKey)) {
      throw new Error(`payer không phải PublicKey: ${typeof payer}`);
    }

    // Đảm bảo các giá trị số hợp lệ
    if (isNaN(amountLamports) || amountLamports <= 0) {
      throw new Error(`amountLamports không hợp lệ: ${amountLamports}`);
    }
    if (isNaN(nonce) || nonce < 0) {
      throw new Error(`nonce không hợp lệ: ${nonce}`);
    }
    if (isNaN(timestamp) || timestamp <= 0) {
      throw new Error(`timestamp không hợp lệ: ${timestamp}`);
    }

    // Log thông tin debug để kiểm tra
    console.log('Tạo transaction chuyển tiền với thông tin:');
    console.log('- multisigPDA:', multisigPDA.toString());
    console.log('- guardianPDA:', guardianPDA.toString());
    console.log('- destination:', destination.toString());
    console.log('- amountLamports:', amountLamports);
    console.log('- nonce:', nonce);
    console.log('- timestamp:', timestamp);
    console.log('- message length:', message.length);
    console.log('- payer:', payer.toString());

    // Discriminator cho verify_and_execute
    const discriminator = Buffer.from([80, 118, 102, 72, 125, 57, 218, 137]);

    // Tham số cho 'action' - chuỗi "transfer"
    const action = "transfer";
    const actionBuffer = Buffer.from(action);
    const actionLenBuffer = Buffer.alloc(4);
    actionLenBuffer.writeUInt32LE(actionBuffer.length, 0);

    // Encode ActionParams
    const amountBuffer = Buffer.alloc(9); // 1 byte cho Option + 8 bytes cho u64
    amountBuffer.writeUInt8(1, 0); // 1 = Some
    const amountBigInt = BigInt(amountLamports);
    for (let i = 0; i < 8; i++) {
      amountBuffer.writeUInt8(Number((amountBigInt >> BigInt(8 * i)) & BigInt(0xFF)), i + 1);
    }

    // Encode destination
    const destinationBuffer = Buffer.alloc(33); // 1 byte cho Option + 32 bytes cho PublicKey
    destinationBuffer.writeUInt8(1, 0); // 1 = Some
    Buffer.from(destination.toBuffer()).copy(destinationBuffer, 1);

    // Encode token_mint (None)
    const tokenMintBuffer = Buffer.alloc(1);
    tokenMintBuffer.writeUInt8(0, 0); // 0 = None

    // Encode nonce (u64, little-endian)
    const nonceBuffer = Buffer.alloc(8);
    const nonceBigInt = BigInt(nonce);
    for (let i = 0; i < 8; i++) {
      nonceBuffer.writeUInt8(Number((nonceBigInt >> BigInt(8 * i)) & BigInt(0xFF)), i);
    }

    // Encode timestamp (i64, little-endian)
    const timestampBuffer = Buffer.alloc(8);
    const timestampBigInt = BigInt(timestamp);
    for (let i = 0; i < 8; i++) {
      timestampBuffer.writeUInt8(Number((timestampBigInt >> BigInt(8 * i)) & BigInt(0xFF)), i);
    }

    // Encode message (vec<u8>)
    const messageLenBuffer = Buffer.alloc(4);
    messageLenBuffer.writeUInt32LE(message.length, 0);
    const messageBuffer = Buffer.from(message);

    // Nối tất cả buffer lại với nhau
    const data = Buffer.concat([
      discriminator,
      actionLenBuffer,
      actionBuffer,
      amountBuffer,
      destinationBuffer,
      tokenMintBuffer,
      nonceBuffer,
      timestampBuffer,
      messageLenBuffer,
      messageBuffer
    ]);

    // Kiểm tra địa chỉ của instruction sysvar
    const sysvarInstructionPubkey = SYSVAR_INSTRUCTIONS_PUBKEY;
    const sysvarClockPubkey = SYSVAR_CLOCK_PUBKEY;

    // Tạo instruction verify_and_execute
    const ix = new TransactionInstruction({
      keys: [
        { pubkey: multisigPDA, isSigner: false, isWritable: true },
        { pubkey: guardianPDA, isSigner: false, isWritable: false },
        { pubkey: sysvarClockPubkey, isSigner: false, isWritable: false },
        { pubkey: sysvarInstructionPubkey, isSigner: false, isWritable: false },
        { pubkey: SystemProgram.programId, isSigner: false, isWritable: false },
        { pubkey: payer, isSigner: true, isWritable: true },
        { pubkey: destination, isSigner: false, isWritable: true }
      ],
      programId: programID,
      data
    });

    // Tạo transaction mới
    return new Transaction().add(ix);
  } catch (error) {
    console.error("Lỗi khi tạo transaction chuyển tiền:", error);
    throw error;
  }
};
