            throw new Error('Guardian không có WebAuthn public key trong account data');
          }
        } else {
          // Nếu không thể đọc được dữ liệu, báo lỗi
          throw new Error('Không thể đọc được public key từ guardian account (dữ liệu quá ngắn)');
        }
      } else {
        // Sử dụng WebAuthn public key từ Firebase
        console.log('Đã tìm thấy WebAuthn public key trong Firebase:', credentialMapping.guardianPublicKey);
        // Chuyển đổi từ mảng số về Buffer
        const webAuthnPubKey = Buffer.from(new Uint8Array(credentialMapping.guardianPublicKey));

        // Kiểm tra độ dài khóa
        if (webAuthnPubKey.length !== 33) {
          console.warn(`WebAuthn public key từ Firebase có độ dài không đúng: ${webAuthnPubKey.length} bytes, cần 33 bytes`);
        }

        // KIỂM TRA CHỮ KÝ SECP256R1 RIÊNG BIỆT
        try {
          setTxStatus(TransactionStatus.SIGNING);
          setSuccess('Đang kiểm tra chữ ký secp256r1...');
          const signatureTestResult = await testSecp256r1Signature(connection, webAuthnPubKey, signature, Buffer.from(messageHashBytes));
          if (signatureTestResult.isValid) {
            console.log('✅ Kiểm tra chữ ký secp256r1 thành công!');
            setSuccess('Kiểm tra chữ ký thành công! Đang tiếp tục giao dịch...');
          } else {
            console.error('❌ Kiểm tra chữ ký secp256r1 thất bại:', signatureTestResult.error);
            setError(`Lỗi khi kiểm tra chữ ký: ${signatureTestResult.error}`);
            setIsTransferring(false);
            setTxStatus(TransactionStatus.ERROR);
            return;
          }
        } catch (testError) {
          console.error('Lỗi khi thực hiện kiểm tra chữ ký:', testError);
          // Tiếp tục quá trình giao dịch ngay cả khi kiểm tra riêng thất bại
          console.log('Tiếp tục giao dịch bình thường mặc dù kiểm tra riêng thất bại...');
        }

        // Tiếp tục quá trình tạo và gửi transaction với cả 2 instruction
        setTxStatus(TransactionStatus.BUILDING_TX);

        // Tạo transaction với verify_and_execute instruction
        const transferTx = createTransferTx(multisigPDA, guardianPDA, destinationPublicKey, amountLamports, nextNonce, timestamp, messageBytes,
        // Sử dụng messageBytes (chưa hash) cho verify_and_execute
        webAuthnPubKey);

        // Đặt secp256r1 instruction là ix đầu tiên (phải đứng trước verify_and_execute)
        transferTx.instructions.unshift(secp256r1Ix);

        // Đặt fee payer và blockhash
        transferTx.feePayer = webAuthnPubKey;
        const {
          blockhash
        } = await connection.getLatestBlockhash();
        transferTx.recentBlockhash = blockhash;

        // Ký transaction bằng fee payer
        transferTx.sign(webAuthnPubKey);

        // Log transaction để debug
        console.log("Transaction info:", {
          feePayer: webAuthnPubKey.toString(),
          instructions: transferTx.instructions.map(ix => ({
            programId: ix.programId.toString(),
            keys: ix.keys.map(k => ({
              pubkey: k.pubkey.toString(),
              isSigner: k.isSigner,
              isWritable: k.isWritable
            })),
            dataSize: ix.data.length
          }))
        });

