import React, { FC, useMemo, useState, useCallback, useEffect } from 'react';
import {
  ConnectionProvider,
  WalletProvider,
  useConnection,
  useWallet
} from '@solana/wallet-adapter-react';
import { WalletAdapterNetwork } from '@solana/wallet-adapter-base';
import {
  PhantomWalletAdapter,
  SolflareWalletAdapter,
  TorusWalletAdapter,
} from '@solana/wallet-adapter-wallets';
import {
  WalletModalProvider,
  WalletMultiButton
} from '@solana/wallet-adapter-react-ui';
import { clusterApiUrl } from '@solana/web3.js';
import { TransferForm } from './components/TransferForm';

import '@solana/wallet-adapter-react-ui/styles.css';
import './App.css';

const App: FC = () => {
  return (
    <Context>
      <Content />
    </Context>
  );
};
export default App;

const Context: FC<React.PropsWithChildren> = ({ children }) => {
  // Mạng Solana
  const network = WalletAdapterNetwork.Devnet;

  // Điểm cuối kết nối
  const endpoint = useMemo(() => clusterApiUrl(network), [network]);

  // Các ví được hỗ trợ
  const wallets = useMemo(
    () => [
      new PhantomWalletAdapter(),
      new SolflareWalletAdapter(),
      new TorusWalletAdapter()
    ],
    [network]
  );

  return (
    <ConnectionProvider endpoint={endpoint}>
      <WalletProvider wallets={wallets} autoConnect>
        <WalletModalProvider>{children}</WalletModalProvider>
      </WalletProvider>
    </ConnectionProvider>
  );
};

const Content: FC = () => {
  const { connection } = useConnection();
  const { publicKey } = useWallet();
  const [balance, setBalance] = useState<number>(0);
  const [transactionStatus, setTransactionStatus] = useState<string | null>(null);

  // Lấy số dư tài khoản
  const getBalance = useCallback(async () => {
    if (!publicKey) {
      setBalance(0);
      return;
    }

    try {
      const bal = await connection.getBalance(publicKey);
      setBalance(bal / 1000000000); // Chuyển đổi lamports sang SOL
    } catch (e) {
      console.error('Lỗi khi lấy số dư:', e);
      setBalance(0);
    }
  }, [connection, publicKey]);

  // Khi publicKey thay đổi, lấy số dư
  useEffect(() => {
    getBalance();
  }, [publicKey, getBalance]);

  // Hàm được gọi khi chuyển tiền thành công
  const handleTransferComplete = useCallback((hash: string) => {
    setTransactionStatus(`Giao dịch thành công! Hash: ${hash}`);
    // Cập nhật số dư sau khi chuyển tiền
    getBalance();

    // Xóa thông báo sau 5 giây
    setTimeout(() => {
      setTransactionStatus(null);
    }, 5000);
  }, [getBalance]);

  return (
    <div className="app-container">
      <header>
        <h1>MoonWallet</h1>
        <WalletMultiButton />
      </header>

      <main>
        {publicKey ? (
          <div className="wallet-info">
            <h2>Thông tin ví</h2>
            <p>Địa chỉ: {publicKey.toString()}</p>
            <p>Số dư: {balance.toFixed(6)} SOL</p>

            <TransferForm 
              publicKey={publicKey}
              balance={balance}
              onTransferComplete={handleTransferComplete}
            />

            {transactionStatus && (
              <div className="transaction-status">
                {transactionStatus}
              </div>
            )}
          </div>
        ) : (
          <div className="connect-prompt">
            <p>Vui lòng kết nối ví để bắt đầu</p>
          </div>
        )}
      </main>
    </div>
  );
};
