        console.log('- Byte đầu tiên:', webAuthnPubKey[0].toString(16));
        console.log('- Hex:', webAuthnPubKey.toString('hex'));
        console.log('\n3. Thông tin signature:');
        console.log('- Độ dài:', normalizedSignature.length, 'bytes');
        console.log('- Hex:', normalizedSignature.toString('hex'));

        // QUAN TRỌNG: Sử dụng message gốc (không hash) cho secp256r1 instruction
        const secp256r1Ix = createSecp256r1Instruction(Buffer.from(messageBytes),
        // Message gốc, không hash
        webAuthnPubKey, normalizedSignature);
        console.log('\n4. Chi tiết instruction secp256r1:');
        console.log('- Program ID:', secp256r1Ix.programId.toString());
        console.log('- Data length:', secp256r1Ix.data.length);
        console.log('- Data hex (first 60 chars):', Buffer.from(secp256r1Ix.data).toString('hex').substring(0, 60) + '...');

        // Tạo ví tạm để trả phí giao dịch
        const feePayer = web3.Keypair.generate();

        // Xin SOL airdrop để trả phí
        try {
          const airdropSignature = await connection.requestAirdrop(feePayer.publicKey, web3.LAMPORTS_PER_SOL / 50 // 0.02 SOL để trả phí
          );
          await connection.confirmTransaction(airdropSignature);

          // Kiểm tra số dư sau khi airdrop
          const feePayerBalance = await connection.getBalance(feePayer.publicKey);
          console.log(`\n5. Thông tin fee payer:`);
          console.log(`- Địa chỉ: ${feePayer.publicKey.toString()}`);
          console.log(`- Số dư: ${feePayerBalance / LAMPORTS_PER_SOL} SOL`);
          if (feePayerBalance === 0) {
            throw new Error('Không thể airdrop SOL cho fee payer');
          }
        } catch (airdropError) {
          console.warn('\n5. Lỗi airdrop SOL:', airdropError);
        }

        // Tạo transaction với verify_and_execute instruction
        const transferTx = createTransferTx(multisigPDA, guardianPDA, destinationPublicKey, amountLamports, nextNonce, timestamp, messageBytes,
        // Sử dụng messageBytes (chưa hash) cho verify_and_execute
        feePayer.publicKey);

        // Đặt secp256r1 instruction là ix đầu tiên
        transferTx.instructions.unshift(secp256r1Ix);

        // Đặt fee payer và blockhash
        transferTx.feePayer = feePayer.publicKey;
        const {
          blockhash
        } = await connection.getLatestBlockhash();
        transferTx.recentBlockhash = blockhash;

        // Ký transaction bằng fee payer
        transferTx.sign(feePayer);
        console.log('\n6. Chi tiết transaction:');
        console.log('- Fee payer:', feePayer.publicKey.toString());
        console.log('- Recent blockhash:', blockhash);
        console.log('- Số lượng instructions:', transferTx.instructions.length);
        console.log('\n7. Chi tiết từng instruction:');
        transferTx.instructions.forEach((ix, index) => {
          console.log(`\nInstruction ${index}:`);
          console.log('- Program ID:', ix.programId.toString());
          console.log('- Data length:', ix.data.length);
          console.log('- Keys:', ix.keys.map(k => ({
            pubkey: k.pubkey.toString(),
            isSigner: k.isSigner,
            isWritable: k.isWritable
          })));
        });

        // Gửi transaction
        setTxStatus(TransactionStatus.SUBMITTING);
        try {
          console.log('\n8. Đang gửi transaction...');
          const transactionId = await connection.sendRawTransaction(transferTx.serialize(), {
            skipPreflight: true,
            preflightCommitment: 'confirmed'
          });
          console.log('Transaction ID:', transactionId);
          setTxId(transactionId);

          // Chờ xác nhận
          setTxStatus(TransactionStatus.CONFIRMING);
          console.log('\n9. Đang chờ xác nhận transaction...');
          const confirmation = await connection.confirmTransaction(transactionId, 'confirmed');
          if (confirmation.value.err) {
            console.error('\n10. Lỗi xác nhận transaction:');
            console.error(JSON.stringify(confirmation.value.err, null, 2));

            // Phân tích lỗi chi tiết
            if (confirmation.value.err.InstructionError) {
              const [index, error] = confirmation.value.err.InstructionError;
              console.error(`\nLỗi tại instruction ${index}:`);
              console.error(error);
              if (error.Custom) {
                console.error(`Custom error code: ${error.Custom}`);
                if (index === 0) {
                  console.error('Lỗi xác thực secp256r1! Kiểm tra lại:');
                  console.error('- Message có đúng không');
                  console.error('- Public key có đúng định dạng không');
                  console.error('- Signature có đúng định dạng không');
                } else {
                  console.error('Lỗi tại instruction của chương trình!');
                }
              }
            }
            throw new Error(`Lỗi khi xác nhận giao dịch: ${JSON.stringify(confirmation.value.err)}`);
          }
          console.log('\n10. Transaction thành công!');
          setTxStatus(TransactionStatus.SUCCESS);
          setSuccess(`Đã chuyển ${amount} SOL đến ${destinationAddress} thành công! ID giao dịch: ${transactionId}`);
          setAmount('');
          setDestinationAddress('');

          // Gọi callback nếu có
          if (onTransferSuccess) {
            onTransferSuccess();
          }
