        const firstByte = webAuthnPubKey[0];
        console.log(`Byte đầu tiên của public key: 0x${firstByte.toString(16)}`);

        // Kiểm tra xem có phải khóa dạng nén không
        if (firstByte !== 0x02 && firstByte !== 0x03) {
          console.warn(`Public key không phải dạng nén! Byte đầu tiên là 0x${firstByte.toString(16)}, phải là 0x02 hoặc 0x03`);
        }

        console.log('Public key chi tiết:');
