        console.log('- Hex:', webAuthnPubKey.toString('hex'));

        console.log('Signature chi tiết:');
        console.log('- Độ dài:', normalizedSignature.length, 'bytes');
        console.log('- Hex:', normalizedSignature.toString('hex'));

        console.log('Message chi tiết:');
        console.log('- Độ dài:', messageBytes.length, 'bytes');
        console.log('- Hex:', Buffer.from(messageBytes).toString('hex'));
        console.log('- Text:', Buffer.from(messageBytes).toString('utf8'));

